"""
Check efficiency of DH and RSA
We got DH from: https://github.com/chrisvoncsefalvay/diffiehellman
We got RSA from python package rsa: pip install rsa
"""
from dh import * 	# Our implementation
from diffiehellman.diffiehellman import DiffieHellman # From pip install diffiehellman
from time import time
from os import urandom
from classRSA import *
import rsa

# This function test our implementation of RSA vs pycrypto
def testRSA():
	bitsizes = [ 64 * 2 ** x for x in range( 1, 6) ]

	for bitsize in bitsizes:
		start = time()
		benchRSA( bitsize )
		print( "Benchmark RSA:", time() - start, "seconds" )

		start = time()
		testOurRSA( bitsize )
		print( "Our RSA:", time() - start, "seconds" )

# This functions is testing all our results in DH
def testingDH():
	"""
	+------------+
	| TESTING DH |
	+------------+
	"""
	# Getting primes with our implementation of sieve of sundaram
	start = time()
	diffieHellman( MIN_NUMBER = 100000, usePycryptoPrime = 0, p = 0 )
	print("Time elapsed using sieve of sundaram:", time() - start, "seconds")

	start = time()
	diffieHellman( MIN_NUMBER = 100000, usePycryptoPrime = 1, p = 0 )
	print("Time elapsed, using pycrypto prime:", time() - start, "seconds")
	# End of prime number time measuring

	# Getting a list of RFC 3526 MODP primes
	primes = loadPrimes()
	groups = [5, 14, 15, 16, 17, 18]

	# Testing predefined primes, our vs a public DH method
	for group in groups:
		start = time()
		diffieHellman( MIN_NUMBER = 2**(640/8), usePycryptoPrime = 0, p = primes[group]['prime'] )
		print("Time elapsed using our DH:", time() - start, "seconds")

		start = time()
		bechDiffieHellman( group = group )
		print("Time elapsed using their DH:", time() - start, "seconds")


def testOurRSA( minBitSizeNumber ):
	n, e, d = Gen( minBitSizeNumber )
	lengthInBytes = int( minBitSizeNumber / 24 )
	msg = int( urandom( lengthInBytes ).hex(), 16 )	# Using the same message length
	ctxt = Enc( [n, e], msg )
	decryptedMessage = Dec( [d, n], ctxt )
	assert( msg == decryptedMessage )

def benchRSA( bitsize = 2048 ):
	pubkey, privkey = rsa.newkeys( bitsize )

	lengthInBytes = int( bitsize / 24 )
	msg = urandom( lengthInBytes ) # Random message with length of 2048 bits

	crypto = rsa.encrypt( msg, pubkey )
	decryptedMessage = rsa.decrypt(crypto, privkey)

	assert( msg == decryptedMessage )

def bechDiffieHellman( group = 5 ):
	alice = DiffieHellman( group = group )
	bob = DiffieHellman( group = group )
	alice.generate_public_key()    # automatically generates private key
	bob.generate_public_key()
	alice_public = alice.generate_shared_secret(bob.public_key, echo_return_key=True)
	bob_public = bob.generate_shared_secret(alice.public_key, echo_return_key=True)

	if alice_public != bob_public:
		raise Exception( "Benchmark Diffie Hellman failed" )

